mapper.select:
select *, ( select ifnull(count(*),0) from comment where comment.b_idx=b.b_idx ) as cmt_count from ( select * , rank() over(order by b_ref desc, b_step asc) as no from board
user_name like concat('%',#{ name },'%')
b_subject like concat('%',#{ subject },'%')
or b_content like concat('%',#{ content },'%')
)b where no between #{ start } and #{ end }

select * from 
(	select 
	*, rank() over(order by b_ref desc, b_step asc) as no
	from board 
)b
 where b_idx=#{ b_idx }
